import React, { Component } from "react";
import Movie from "./Movie";
import { getMovies } from "../services/fakeMovieService";
import Pagination from "./common/Pagination";

class Movies extends Component {
  state = {
    movies: getMovies(),
    currentPage: 1,
    moviesPerPage: 2,
  };

  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies });
  };

  handlePaginate = (pageNumber) => this.setState({ currentPage: pageNumber });

  handleLike = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  render() {
    const { length: count } = this.state.movies;
    // Get current movies
    const indexOfLastMovie = this.state.currentPage * this.state.moviesPerPage;
    const indexOfFirstMovie = indexOfLastMovie - this.state.moviesPerPage;
    const currentMovies = this.state.movies.slice(
      indexOfFirstMovie,
      indexOfLastMovie
    );

    if (count === 0) return <p>There are no movies in the database.</p>;

    return (
      <React.Fragment>
        <p>Showing {count} movies in the database.</p>
        <table className="table">
          <thead>
            <tr key={0}>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((movie) => (
              <Movie
                key={movie._id}
                movie={movie}
                onDelete={this.handleDelete}
                onLike={this.handleLike}
              />
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={count}
          pageSize={this.state.moviesPerPage}
          currentPage={this.state.currentPage}
          onPaginate={this.handlePaginate}
        />
      </React.Fragment>
    );
  }
}

export default Movies;
