import React, { Component } from "react";

class TableHeader extends Component {
  raiseSort = path => {
    const sortColumn = { ...this.props.sortColumn };
    if (sortColumn.path === path) {
      sortColumn.order = sortColumn.order === "asc" ? "desc" : "asc";
    } else {
      sortColumn.path = path;
      sortColumn.order = "asc";
    }
    this.props.onSort(sortColumn);
  };

  render() {
    console.log(columnNames, columnProperties);

    return (
      <thead>
        <tr key={0}>
          <th onClick={() => this.raiseSort("title")} style={{ cursor: "pointer" }}>
            Title
          </th>
          <th onClick={() => this.raiseSort("genre.name")} style={{ cursor: "pointer" }}>
            Genre
          </th>
          <th onClick={() => this.raiseSort("numberInStock")} style={{ cursor: "pointer" }}>
            Stock
          </th>
          <th onClick={() => this.raiseSort("dailyRentalRate")} style={{ cursor: "pointer" }}>
            Rate
          </th>
          <th></th>
          <th></th>
        </tr>
      </thead>
    );
  }
}

export default TableHeader;
