import React, { Component } from "react";
import Movie from "./Movie";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import Pagination from "./common/Pagination";
import { paginate } from "./utils/paginate";
import ListGroup from "./common/ListGroup";
import { changeGroup } from "./utils/changeGroup";

class Movies extends Component {
  state = {
    movies: getMovies(),
    genres: getGenres(),
    currentPage: 1,
    pageSize: 2,
    allGenres: "All Genres",
    currentGroup: "All Genres",
  };

  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLike = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  handlePageChange = (page) => this.setState({ currentPage: page });

  handleGroupChange = (group) => this.setState({ currentGroup: group });

  render() {
    const {
      movies: allMovies,
      genres,
      currentPage,
      pageSize,
      currentGroup,
      allGenres,
    } = this.state;

    const groupedMovies = changeGroup(allMovies, currentGroup, allGenres);
    const { length: count } = groupedMovies;
    if (count === 0) return <p>There are no movies in the database.</p>;
    const movies = paginate(groupedMovies, pageSize, currentPage);

    return (
      <React.Fragment>
        <ListGroup
          allName={allGenres}
          groups={genres}
          currentGroup={currentGroup}
          onGroupChange={this.handleGroupChange}
        />

        <p>Showing {movies.length} movies in the database.</p>
        <table className="table">
          <thead>
            <tr key={0}>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie) => (
              <Movie
                key={movie._id}
                movie={movie}
                onDelete={this.handleDelete}
                onLike={this.handleLike}
              />
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={groupedMovies.length}
          pageSize={pageSize}
          currentPage={currentPage}
          onPageChange={this.handlePageChange}
        />
      </React.Fragment>
    );
  }
}

export default Movies;
