import React from "react";

const ListGroup = (props) => {
  const { all, groups, currentGroup, onGroupChange } = props;

  return (
    <ul className="list-group mb-4">
      <li
        key={all}
        onClick={() => onGroupChange(all)}
        style={{ cursor: "pointer" }}
        className={
          all === currentGroup ? "list-group-item active" : "list-group-item"
        }
      >
        {all}
      </li>

      {groups.map((group) => (
        <li
          key={group._id}
          onClick={() => onGroupChange(group.name)}
          style={{ cursor: "pointer" }}
          className={
            group.name === currentGroup
              ? "list-group-item active"
              : "list-group-item"
          }
        >
          {group.name}
        </li>
      ))}
    </ul>
  );
};

export default ListGroup;
