import React, { Component } from "react";
import Movie from "./Movie";
import TableHeader from "./common/TableHeader";
import TableBody from "./common/TableBody";

class MoviesTable extends Component {
  columns = [
    { path: "title", label: "Title" },
    { path: "genre.name", label: "Genres" },
    { path: "numberInStock", label: "Stock" },
    { path: "dailyRentalRate", label: "Rate" },
    { key: "Like", content: movie => <Like liked={movie.liked} onClick={() => onLike(movie)} /> },
    {
      key: "Delete",
      content: movie => (
        <button onClick={() => onDelete(movie)} className="btn btn-danger btn-sm">
          Delete
        </button>
      ),
    },
  ];

  render() {
    const { movies, onDelete, onLike, onSort, sortColumn } = this.props;

    return (
      <table className="table">
        <TableHeader onSort={onSort} sortColumn={sortColumn} columns={this.columns} />
        <TableBody data={movies} columns={this.columns} onDelete={onDelete} onLike={onLike} />
      </table>
    );
  }
}

export default MoviesTable;
