import React, { Component } from "react";
import Movie from "./Movie";

class MoviesTable extends Component {
  raiseSort = path => {
    console.log("RaiseSort");
    const sortColumn = { ...this.props.sortColumn };
    console.log("sortColumn before:", sortColumn);

    if (sortColumn.path === path) {
      sortColumn.order = sortColumn.order === "asc" ? "desc" : "asc";
    } else {
      sortColumn.path = path;
      sortColumn.order = "asc";
    }
    this.props.onSort(sortColumn);
  };

  render() {
    const { movies, onDelete, onLike } = this.props;

    return (
      <table className="table">
        <thead>
          <tr key={0}>
            <th onClick={() => this.raiseSort("title")} style={{ cursor: "pointer" }}>
              Title
            </th>
            <th onClick={() => this.raiseSort("genre.name")} style={{ cursor: "pointer" }}>
              Genre
            </th>
            <th onClick={() => this.raiseSort("numberInStock")} style={{ cursor: "pointer" }}>
              Stock
            </th>
            <th onClick={() => this.raiseSort("dailyRentalRate")} style={{ cursor: "pointer" }}>
              Rate
            </th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {movies.map(movie => (
            <Movie key={movie._id} movie={movie} onDelete={onDelete} onLike={onLike} />
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
