import React from "react";

const ListGroup = (props) => {
  const { groups, currentGroup, onChangeGroup } = props;

  return (
    <ul className="list-group mb-4">
      <li
        className={
          "All Generes" === currentGroup
            ? "list-group-item active"
            : "list-group-item"
        }
      >
        All Generes
      </li>
      {groups.map((group) => (
        <li
          key={group._id}
          className={
            group === currentGroup
              ? "list-group-item active"
              : "list-group-item"
          }
        >
          {group.name}
        </li>
      ))}
    </ul>
  );
};

export default ListGroup;
