import React, { Component } from "react";
import Movie from "./Movie";
import { getMovies } from "../services/fakeMovieService";
import { getGenres } from "../services/fakeGenreService";
import Pagination from "./common/Pagination";
import { paginate } from "./utils/paginate";
import ListGroup from "./common/ListGroup";
import { changeGroup } from "./utils/changeGroup";

class Movies extends Component {
  state = {
    movies: [],
    genres: [],
    currentPage: 1,
    pageSize: 3,
    selectedGenre: null,
    // selectedGenre: "",
    // allGenres: "All Genres",
    // currentGroup: "All Genres",
  };

  componentDidMount() {
    this.setState({
      movies: getMovies(),
      genres: [{ _id: "0", name: "All Genres" }, ...getGenres()],
    });
  }

  handleDelete = movie => {
    const movies = this.state.movies.filter(m => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLike = movie => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  handlePageChange = page => this.setState({ currentPage: page });

  handleGenreSelect = genre => {
    this.setState({ selectedGenre: genre });
    // console.log(genre);
  };

  render() {
    const { movies: allMovies, currentPage, pageSize, selectedGenre } = this.state;
    const { length: count } = this.state.movies;

    if (count === 0) return <p>There are no movies in the database.</p>;

    const filtered = selectedGenre
      ? allMovies.filter(m => m.genre._id === selectedGenre._id)
      : allMovies;
    console.log(filtered);
    const movies = paginate(filtered, pageSize, currentPage);

    return (
      <div className="row">
        <div className="col-2">
          <ListGroup
            items={this.state.genres}
            selectedItem={this.state.selectedGenre}
            onItemSelect={this.handleGenreSelect}
          />
        </div>
        <div className="col">
          <p>Showing {filtered.length} movies in the database.</p>
          <table className="table">
            <thead>
              <tr key={0}>
                <th>Title</th>
                <th>Genre</th>
                <th>Stock</th>
                <th>Rate</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {movies.map(movie => (
                <Movie
                  key={movie._id}
                  movie={movie}
                  onDelete={this.handleDelete}
                  onLike={this.handleLike}
                />
              ))}
            </tbody>
          </table>
          <Pagination
            itemsCount={filtered.length}
            pageSize={pageSize}
            currentPage={currentPage}
            onPageChange={this.handlePageChange}
          />
        </div>
        {/* <ListGroup all={allGenres} groups={genres} currentGroup={currentGroup} onGroupChange={this.handleGroupChange} /> */}
      </div>
    );
  }
}

export default Movies;
