import React from "react";
import { Component } from "react";
import Movies from "./Components/Movies";
import { getMovies } from "./services/fakeMovieService";
import Pagination from "./Components/common/Pagination";

class App extends Component {
  state = {
    movies: getMovies(),
    currentPage: 1,
    moviesPerRange: 3,
  };

  render() {
    // Get current items
    const indexOfLastMovie = this.state.currentPage * this.state.moviesPerRange;
    const indexOfFirstMovie = indexOfLastMovie - this.state.moviesPerRange;
    const currentItems = this.state.movies.slice(
      indexOfFirstItem,
      indexOfLastItem
    );

    return (
      <main className="container">
        <Movies movies={currentItems} />
        <Pagination curr />
      </main>
    );
  }
}

export default App;
