import React from "react";

const ListGroup = (props) => {
  const { allName, groups, currentGroup, onChangeGroup } = props;

  return (
    <ul className="list-group mb-4">
      <li
        // onClick={onChangeGroup}
        style={{ cursor: "pointer" }}
        className={
          allName === currentGroup
            ? "list-group-item active"
            : "list-group-item"
        }
      >
        {allName}
      </li>
      {groups.map((group) => (
        <li
          // onClick={onChangeGroup}
          style={{ cursor: "pointer" }}
          key={group._id}
          className={
            group === currentGroup
              ? "list-group-item active"
              : "list-group-item"
          }
        >
          {group.name}
        </li>
      ))}
    </ul>
  );
};

export default ListGroup;
