import React, { Component } from "react";
import Movie from "./Movie";
import { getMovies } from "../services/fakeMovieService";
import Pagination from "./common/Pagination";

class Movies extends Component {
  state = {
    movies: getMovies(),
    currentPage: 1,
    pageSize: 2,
  };

  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies });
  };

  handleLike = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };

  handlePageChange = (pageNumber) => this.setState({ currentPage: pageNumber });

  render() {
    const { movies, currentPage, pageSize } = this.state;
    const { length: count } = movies;
    // Get current movies
    const indexOfLastMovie = currentPage * pageSize;
    const indexOfFirstMovie = indexOfLastMovie - pageSize;
    const currentMovies = movies.slice(indexOfFirstMovie, indexOfLastMovie);

    if (count === 0) return <p>There are no movies in the database.</p>;

    return (
      <React.Fragment>
        <p>Showing {currentMovies.length} movies in the database.</p>
        <table className="table">
          <thead>
            <tr key={0}>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {currentMovies.map((movie) => (
              <Movie
                key={movie._id}
                movie={movie}
                onDelete={this.handleDelete}
                onLike={this.handleLike}
              />
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={count}
          pageSize={pageSize}
          currentPage={currentPage}
          onPageChange={this.handlePageChange}
        />
      </React.Fragment>
    );
  }
}

export default Movies;
